#include <stdio.h>
#include <string.h>

void reverse(char *s) {
    int n = strlen(s);
    for (int i = 0, j = n - 1; i < j; i++, j--) {
        char t = s[i]; s[i] = s[j]; s[j] = t;
    }
}

int main(void) {
    char buf[64] = "Hello, World!";
    reverse(buf);
    printf("%s\n", buf);
    return 0;
}

#include <stdio.h>
#include <stdlib.h>

typedef struct Node {
    int val;
    struct Node *next;
} Node;

Node* push(Node* head, int v) {
    Node* n = malloc(sizeof(Node));
    n->val = v; n->next = head;
    return n;
}

void print(Node* head){
    for(Node* p=head; p; p=p->next) printf("%d ", p->val);
    printf("\n");
}

void free_list(Node* head){
    while(head){ Node* t=head; head=head->next; free(t);}
}

int main(void){
    Node* head = NULL;
    for(int i=0;i<5;i++) head = push(head, i*i);
    print(head);
    free_list(head);
    return 0;
}

