from dataclasses import dataclass
from typing import Optional

@dataclass
class Node:
    val: int
    next: Optional['Node'] = None

def push(head: Optional[Node], v: int) -> Node:
    return Node(v, head)


def print_list(head: Optional[Node]) -> None:
    p = head
    out = []
    while p:
        out.append(str(p.val))
        p = p.next
    print(" ".join(out))

head = None
for i in range(5):
    head = push(head, i*i)
print_list(head)
